mapper.select:
select a.staff_id,a.staff_name,a.staff_code,a.password,a.staff_phone,a.staff_type,a.staff_state,a.staff_email,a.operator_id,a.remark , group_concat(c.right_name separator '„ÄÅ') as right_names from CRM_STAFF a left join CRM_STAFF_RIGHT b on b.staff_id = a.staff_id left join CRM_RIGHT c on c.right_id = b.right_id where a.STAFF_STATE = '1'
and a.operator_id = #{operatorId}
group by a.staff_id;

mapper.update:
update CRM_STAFF
set staff_name = #{staffName}
    ,staff_code = #{staffCode}
    ,password = #{password}
    ,staff_phone = #{staffPhone}
    ,staff_type = #{staffType}
    ,staff_state = #{staffState}
    ,staff_email = #{staffEmail}
    ,remark = #{remark}
where staff_id = #{staffId}

UPDATE CRM_STAFF
SET staff_state = #{staffState}
WHERE staff_id = #{staffId}

UPDATE CRM_STAFF
SET staff_state = #{staffState} ,operator_id = #{operatorId}
WHERE staff_id = #{staffId}

mapper.select:
select RIGHT_ID
from CRM_STAFF_RIGHT
where STAFF_ID = #{staffId}
